def area():
    length = float(input('请输入矩形的长：'))
    width = float(input('请输入矩形的宽：'))
    print(f'矩形的长是：{length}n矩形的宽是：{width}n矩形的面积是：{length * width}')
def perimeter():
    length = float(input('请输入矩形的长：'))
    width = float(input('请输入矩形的宽：'))
    print(f'矩形的长是：{length}n矩形的宽是：{width}n矩形的面积是：{2 * (length + width)}')